import React, { useState, ChangeEvent, useEffect } from 'react';
import './PdfUploader.css';

const PdfUploader: React.FC = () => {
  const [file, setFile] = useState<File | null>(null);
  const [isLoading, setIsLoading] = useState<boolean>(false);
  const [isDone, setIsDone] = useState<boolean>(false);
  const [progress, setProgress] = useState<number>(0);

  const handleFileChange = (event: ChangeEvent<HTMLInputElement>) => {
    if (event.target.files && event.target.files[0]) {
      setFile(event.target.files[0]);
    }
  };

  const handleUpload = async () => {
    if (!file) return;

    setIsLoading(true);
    setIsDone(false);
    setProgress(0);

    const formData = new FormData();
    formData.append('pdf', file);  // 'pdf' is the key you will use to retrieve the file on the backend.

    try {
      const response = await fetch('http://your-backend-endpoint/upload', {
        method: 'POST',
        body: formData,
        headers: {
          // 'Content-Type': 'multipart/form-data' is automatically set when using FormData
        },
      });

      if (!response.ok) {
        throw new Error('Upload failed');
      }

      // Simulate progress, replace this with actual progress tracking if your API supports it
      for (let i = 0; i <= 100; i += 2) {
        await new Promise(resolve => setTimeout(resolve, 50));  // Simulating network delay
        setProgress(i);
      }

      setIsDone(true);
    } catch (error) {
      console.error('Error uploading file:', error);
    } finally {
      setIsLoading(false);
    }
  };

  useEffect(() => {
    if (isDone) {
      const timer = setTimeout(() => setIsDone(false), 3000);
      return () => clearTimeout(timer);
    }
  }, [isDone]);

  const renderProgressBar = (progress: number) => {
    const barWidth = 50;
    const filledWidth = Math.floor((progress / 100) * barWidth);
    const emptyWidth = barWidth - filledWidth;
    return `${progress}% [${'■'.repeat(filledWidth)}${''.repeat(emptyWidth)}]`;
  };

  return (
    <div className="pdf-uploader">
      <p>{'> SELECT PDF FILE:'}</p>
      <input
        type="file"
        accept=".pdf"
        onChange={handleFileChange}
        className="file-input"
      />
      <button
        onClick={handleUpload}
        disabled={!file || isLoading}
        className="upload-button"
      >
        {isLoading ? 'UPLOADING...' : 'UPLOAD PDF'}
      </button>
      {isLoading && (
        <div className="progress-container">
          <p>{'UPLOADING...'}</p>
          <div className="progress-bar">
            {renderProgressBar(progress)}
          </div>
        </div>
      )}
      {isDone && (
        <div className="completion-message">
          {'> UPLOAD COMPLETE'}
        </div>
      )}
    </div>
  );
};

export default PdfUploader;
