import React, { useState, useEffect, useRef, ChangeEvent, FormEvent } from 'react';
import './RAGApplication.css';

// Declare Three.js as a global variable since we're loading it from a CDN
declare global {
  interface Window { THREE: any; }
}

interface FileInfo {
  name: string;
}

enum AppState {
  Upload,
  Embedding,
  Chat
}

const RAGApplication: React.FC = () => {
  const [files, setFiles] = useState<FileInfo[]>([]);
  const [question, setQuestion] = useState<string>('');
  const [answer, setAnswer] = useState<string>('');
  const [appState, setAppState] = useState<AppState>(AppState.Upload);

  const [progress, setProgress] = useState<number>(0);


  const animationRef = useRef<HTMLDivElement>(null);


  const canvasRef = useRef<HTMLCanvasElement>(null);

  useEffect(() => {
    if (appState === AppState.Embedding && animationRef.current) {
      loader();
    }
    const scene = new window.THREE.Scene();
    const camera = new window.THREE.PerspectiveCamera(35, window.innerWidth / window.innerHeight, 0.1, 1000);
    const renderer = new window.THREE.WebGLRenderer({ canvas: canvasRef.current, alpha: true });
    renderer.setSize(window.innerWidth, window.innerHeight);
    const geometry = new window.THREE.SphereGeometry(1, 32, 32);
    const material = new window.THREE.MeshBasicMaterial({ color: 0xDCDCDC, wireframe: true });
    const sphere = new window.THREE.Mesh(geometry, material);
    scene.add(sphere);
    camera.position.z = 5;
    const animate = () => {
      requestAnimationFrame(animate);
      sphere.rotation.x += 0.002;
      sphere.rotation.y -= 0.002;
      renderer.render(scene, camera);
    };
    animate();
    const handleResize = () => {
      const width = window.innerWidth;
      const height = window.innerHeight;
      renderer.setSize(width, height);
      camera.aspect = width / height;
      camera.updateProjectionMatrix();
    };
    window.addEventListener('resize', handleResize);
    return () => {
      window.removeEventListener('resize', handleResize);
    };
  }, [appState]);

  const loader = async () => {
    setProgress(0);
    for (let i = 0; i <= 100; i += 2) {
      await new Promise(resolve => setTimeout(resolve, 100));
      setProgress(i);
    }
  }



  const handleFileUpload = (event: ChangeEvent<HTMLInputElement>) => {
    const uploadedFiles = event.target.files;
    if (uploadedFiles) {
      const newFiles = Array.from(uploadedFiles).map(file => ({ name: file.name }));
      setFiles(prevFiles => [...prevFiles, ...newFiles]);
    }
  };

  const handleEmbed = () => {
    setAppState(AppState.Embedding);
    // Simulate embedding process
    setTimeout(() => {
      setAppState(AppState.Chat);
    }, 5000); // 5 seconds for demonstration
  };

  const handleQuestionSubmit = (event: FormEvent<HTMLFormElement>) => {
    event.preventDefault();
    // Here you would typically send the question to your backend
    setAnswer(`Here's the answer to your question: "${question}"`);
    setQuestion('');
  };

  const renderProgressBar = (progress: number) => {
    const barWidth = 50;
    const filledWidth = Math.floor((progress / 100) * barWidth);
    const emptyWidth = barWidth - filledWidth;
    return `${progress}% [${'â– '.repeat(filledWidth)}${''.repeat(emptyWidth)}]`;
  };

  return (
    <div className="rag-application">
        <canvas ref={canvasRef} className="background-canvas" />
      <div className="container">
        <h1 className="title">RAG Application</h1>

        {appState === AppState.Upload && (
          <div className="section">
            <h2 className="section-title">Upload PDFs</h2>
            <label className="file-upload">
              <span>Drop files to upload, or click to browse</span>
              <input type="file" onChange={handleFileUpload} multiple accept=".pdf" />
            </label>
            {files.length > 0 && (
              <div className="file-list">
                <h3 className="file-list-title">Uploaded Files:</h3>
                <ul>
                  {files.map((file, index) => (
                    <li key={index} className="file-item">
                      <span>{file.name}</span>
                    </li>
                  ))}
                </ul>
                <button className="embed-button" onClick={handleEmbed}>Embed PDFs</button>
              </div>
            )}
          </div>
        )}

        {appState === AppState.Embedding && (
          <div className="loading-animation" ref={animationRef}>


            <div className="progress-container">
              <p>{'> UPLOADING FILE...'}</p>
              <div className="progress-bar">
                {renderProgressBar(progress)}
              </div>
            </div>



            </div>
        )}

        {appState === AppState.Chat && (
          <>
            <div className="section">
              <h2 className="section-title">Ask a Question</h2>
              <form onSubmit={handleQuestionSubmit} className="question-form">
                <input
                  type="text"
                  value={question}
                  onChange={(e: ChangeEvent<HTMLInputElement>) => setQuestion(e.target.value)}
                  placeholder="Enter your question here"
                  className="question-input"
                />
                <button type="submit" className="submit-button">
                  Submit Question
                </button>
              </form>
            </div>

            {answer && (
              <div className="section">
                <h2 className="section-title">Answer</h2>
                <div className="answer-box">
                  <p>{answer}</p>
                </div>
              </div>
            )}
          </>
        )}
      </div>
    </div>
  );
};

export default RAGApplication;
