import React, { useState, ChangeEvent } from 'react';

const SimplePdfUploader: React.FC = () => {
  const [file, setFile] = useState<File | null>(null);  // State to store the selected file
  const [message, setMessage] = useState<string>('');  // State to store upload status messages

  // Handle file selection
  const handleFileChange = (event: ChangeEvent<HTMLInputElement>) => {
    if (event.target.files && event.target.files[0]) {
      setFile(event.target.files[0]); 
    }
  };

  const handleUpload = async () => {
    if (!file) {
      setMessage('Please select a PDF file to upload.');
      return;
    }

    const formData = new FormData();
    formData.append('pdf', file);

    try {
      const response = await fetch('http://localhost:5000/upload', {
        method: 'POST',
        body: formData,
      });

      if (!response.ok) {
        throw new Error('Upload failed');
      }

      setMessage('Upload successful!');
    } catch (error) {
      setMessage(`Error uploading file: ${error}`);
    }
  };

  return (
    <div className="pdf-uploader">
      <input
        type="file"
        accept=".pdf" 
        onChange={handleFileChange}  // Trigger file selection
      />
      <button onClick={handleUpload} disabled={!file}>Upload PDF</button>  
      {message && <p>{message}</p>}
    </div>
  );
};

export default SimplePdfUploader;
