import React, { useState, useEffect } from 'react';
import './loader.css';

interface CubeFaceStyles {
  transform: string;
}

const ThreeDPercentageLoader = () => {
  const [percentage, setPercentage] = useState<number>(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setPercentage((prevPercentage) => (prevPercentage === 100 ? 0 : prevPercentage + 1));
    }, 50);

    return () => clearInterval(interval);
  }, []);

  const getCubeFaceStyles = (side: string): CubeFaceStyles => {
    switch (side) {
      case 'front': return { transform: 'translateZ(2rem)' };
      case 'back': return { transform: 'translateZ(-2rem) rotateY(180deg)' };
      case 'right': return { transform: 'translateX(2rem) rotateY(90deg)' };
      case 'left': return { transform: 'translateX(-2rem) rotateY(-90deg)' };
      case 'top': return { transform: 'translateY(-2rem) rotateX(90deg)' };
      case 'bottom': return { transform: 'translateY(2rem) rotateX(-90deg)' };
      default: return { transform: '' };
    }
  };

  return (
    <div className="loader-container">
      <div className="loader">
        <svg className="progress-ring" viewBox="0 0 100 100">
          <circle
            className="progress-ring__circle progress-ring__circle--bg"
            cx="50"
            cy="50"
            r="45"
          />
          <circle
            className="progress-ring__circle progress-ring__circle--fg"
            cx="50"
            cy="50"
            r="45"
            style={{
              strokeDasharray: `${percentage * 2.83}, 283`,
            }}
          />
        </svg>
        <div className="percentage">{percentage}%</div>
        {[0, 1, 2, 3].map((i) => (
          <div
            key={i}
            className="cube"
            style={{ animationDelay: `${i * 0.5}s` }}
          >
            {['front', 'back', 'right', 'left', 'top', 'bottom'].map((side) => (
              <div
                key={side}
                className={`cube__face cube__face--${side}`}
                style={getCubeFaceStyles(side)}
              />
            ))}
          </div>
        ))}
      </div>
    </div>
  );
};

export default ThreeDPercentageLoader;
