import React, { useState, ChangeEvent, useEffect } from 'react';

const PdfUploader: React.FC = () => {
  const [file, setFile] = useState<File | null>(null);
  const [isLoading, setIsLoading] = useState<boolean>(false);
  const [isDone, setIsDone] = useState<boolean>(false);
  const [progress, setProgress] = useState<number>(0);

  const handleFileChange = (event: ChangeEvent<HTMLInputElement>) => {
    if (event.target.files && event.target.files[0]) {
      setFile(event.target.files[0]);
    }
  };

  const handleUpload = async () => {
    if (!file) return;

    setIsLoading(true);
    setIsDone(false);
    setProgress(0);

    // Simulating upload process with progress
    for (let i = 0; i <= 100; i += 5) {
      await new Promise(resolve => setTimeout(resolve, 100));
      setProgress(i);
    }

    setIsLoading(false);
    setIsDone(true);
  };

  useEffect(() => {
    if (isDone) {
      const timer = setTimeout(() => setIsDone(false), 3000);
      return () => clearTimeout(timer);
    }
  }, [isDone]);

  return (
    <div className="p-4 max-w-md mx-auto">
      <input
        type="file"
        accept=".pdf"
        onChange={handleFileChange}
        className="mb-4 p-2 border rounded w-full"
      />
      <button
        onClick={handleUpload}
        disabled={!file || isLoading}
        className="bg-blue-500 text-white p-2 rounded w-full disabled:bg-gray-300"
      >
        Upload PDF
      </button>
      {isLoading && (
        <div className="mt-4">
          <div className="w-full bg-gray-200 rounded-full h-2.5 dark:bg-gray-700">
            <div 
              className="bg-blue-600 h-2.5 rounded-full transition-all duration-300 ease-in-out"
              style={{ width: `${progress}%` }}
            ></div>
          </div>
          <p className="text-center mt-2">{`${progress}%`}</p>
        </div>
      )}
      {isDone && (
        <div className="mt-4 text-center text-green-500 animate-pulse">
          Upload Complete!
        </div>
      )}
    </div>
  );
};

export default PdfUploader;
