import React, { useState, ChangeEvent, useEffect } from 'react';
import './PdfUploader.css';
import QuestionForm from './QuestionForm';  // Import the QuestionForm component

const PdfUploader: React.FC = () => {
  const [file, setFile] = useState<File | null>(null);
  const [isLoading, setIsLoading] = useState<boolean>(false);
  const [isDone, setIsDone] = useState<boolean>(false);
  const [progress, setProgress] = useState<number>(0);
  const [message, setMessage] = useState<string>('');  
  const [isQuestioning, setIsQuestioning] = useState<boolean>(false);  // State for question-asking view

  const handleFileChange = (event: ChangeEvent<HTMLInputElement>) => {
    if (event.target.files && event.target.files[0]) {
      setFile(event.target.files[0]);
    }
  };

  const handleUpload = async () => {
    if (!file) {
      setMessage('Please select a PDF file to upload.');
      return;
    }
    setIsLoading(true);
    setIsDone(false);
    setProgress(0);

    const formData = new FormData();
    formData.append('pdf', file);
    try {
      const response = await fetch('http://localhost:5000/upload', {
        method: 'POST',
        body: formData,
      });
      if (!response.ok) {
        throw new Error('Upload failed');
      }
      setMessage('Upload successful!');
    } catch (error) {
      setMessage(`Error uploading file: ${error}`);
    }

    // Simulating upload process with progress
    for (let i = 0; i <= 100; i += 2) {
      await new Promise(resolve => setTimeout(resolve, 60));
      setProgress(i);
    }
    setIsLoading(false);
    setIsDone(true);
    setIsQuestioning(true);  // Switch to the questioning state
  };

  useEffect(() => {
    if (isDone) {
      const timer = setTimeout(() => setIsDone(false), 3500);
      return () => clearTimeout(timer);
    }
  }, [isDone]);

  const renderProgressBar = (progress: number) => {
    const barWidth = 50;
    const filledWidth = Math.floor((progress / 100) * barWidth);
    const emptyWidth = barWidth - filledWidth;
    return `${progress}% [${'■'.repeat(filledWidth)}${''.repeat(emptyWidth)}]`;
  };

  // Render the QuestionForm if isQuestioning is true
  if (isQuestioning) {
    return <QuestionForm onClose={() => setIsQuestioning(false)} />;  // Close button functionality
  }

  return (
    <div className="pdf-uploader">
      <p>{'> SELECT PDF FILE:'}</p>
      <input
        type="file"
        accept=".pdf"
        onChange={handleFileChange}
        className="file-input"
      />
      <button
        onClick={handleUpload}
        disabled={!file}
        className="upload-button"
      >
        {isLoading ? 'UPLOADING...' : 'UPLOAD PDF'}
      </button>
      {isLoading && (
        <div className="progress-container">
          <p>{'UPLOADING...'}</p>
          <div className="progress-bar">
            {renderProgressBar(progress)}
          </div>
        </div>
      )}
      {isDone && (
        <div className="completion-message">
          {'> UPLOAD COMPLETE'}
        </div>
      )}
      {message && <p>{message}</p>}  {/* Display status messages */}
    </div>
  );
};

export default PdfUploader;
