import React, { useState, useEffect } from 'react';
import './Loader.css';

const Loader = () => {
  const [percentage, setPercentage] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setPercentage(prev => prev === 100 ? 0 : prev + 1);
    }, 50);
    return () => clearInterval(interval);
  }, []);

  const getCubeFaceStyles = (side) => {
    const transforms = {
      front: 'translateZ(2rem)',
      back: 'translateZ(-2rem) rotateY(180deg)',
      right: 'translateX(2rem) rotateY(90deg)',
      left: 'translateX(-2rem) rotateY(-90deg)',
      top: 'translateY(-2rem) rotateX(90deg)',
      bottom: 'translateY(2rem) rotateX(-90deg)'
    };
    return { transform: transforms[side] || '' };
  };

  return (
    <div className="loader-container">
      <div className="loader">
        <svg className="progress-ring" viewBox="0 0 100 100">
          <circle className="progress-ring__circle progress-ring__circle--bg" cx="50" cy="50" r="45" />
          <circle
            className="progress-ring__circle progress-ring__circle--fg"
            cx="50"
            cy="50"
            r="45"
            style={{ strokeDasharray: `${percentage * 2.83}, 283` }}
          />
        </svg>
        <div className="percentage">{percentage}%</div>
        {[...Array(4)].map((_, i) => (
          <div key={i} className="cube" style={{ animationDelay: `${i * 0.5}s` }}>
            {['front', 'back', 'right', 'left', 'top', 'bottom'].map(side => (
              <div key={side} className={`cube__face cube__face--${side}`} style={getCubeFaceStyles(side)} />
            ))}
          </div>
        ))}
      </div>
    </div>
  );
};

export default Loader;
